import React from 'react';
import './SpyTable.css';
import { MDBTable, MDBTableBody, MDBTableHead } from 'mdbreact';
import {connect} from 'react-redux';
import { selectStock } from '../../State/actions';
import '@fortawesome/fontawesome-free/css/all.min.css';

const SpyTable = (props) => {
    const {spy} = props;

    console.log(props.stock)

    const changeColor = (ticker, leftRow) => {
		if (!leftRow) {
			document.getElementById(ticker).style.backgroundColor = 'rgb(200,200,200)';
		} else {
			document.getElementById(ticker).style.backgroundColor = 'rgb(237, 237, 237)'
		}
	}

    return <MDBTable maxHeight='25rem' className="heldTable" rules='none' scrollY border='1'>
        <MDBTableHead>
            <tr className='heldToprow'>
                <td className="heldTd">Company</td>
                <td className="heldTd">Ticker</td>
                <td className="heldTd">Price</td>
                <td className="heldTd">Weight</td>
            </tr>
        </MDBTableHead>
        <MDBTableBody>
            {spy.map((stock, i) => {
                return <tr key={i} className="heldTr"
                    id={stock.Symbol}
                    onClick={() => console.log(stock.Symbol)}
                    onMouseOver={() => changeColor(stock.Symbol, false)}
					onMouseOut={() => changeColor(stock.Symbol, true)}
                >
                    <td className="heldTd">{stock.Company}</td>
                    <td className="heldTd">{stock.Symbol}</td>
                    <td className="heldTd">{stock.Price}</td>
                    <td className="heldTd">{stock.Weight.toFixed(2)}%</td>
                </tr>
            })
            
            }
        </MDBTableBody>
    </MDBTable>
}

export default SpyTable;
