import { createSlice } from "@reduxjs/toolkit";

export const spySlice = createSlice({
    name: 'spy',
    initialState: {
        value: {}
    },
    reducers: {
        setSpy: (state, action) => {
            state.value = action.payload;
        }
    }
});

export const { setSpy } = spySlice.actions;
export default spySlice.reducer;
