import React, { useEffect } from 'react';
import './SpyTable.css';
import { MDBTable, MDBTableBody, MDBTableHead } from 'mdbreact';
import '@fortawesome/fontawesome-free/css/all.min.css';

const SpyTable = (props) => {
    const {spy, ticker, setTicker} = props;

    const changeColor = (ticker, leftRow) => {
		if (!leftRow) {
			document.getElementById(ticker).style.backgroundColor = 'rgb(200,200,200)';
		} else {
			document.getElementById(ticker).style.backgroundColor = 'rgb(237, 237, 237)'
		}
	}

    const spyTableClick = (symbol) => {
        setTicker(symbol);
    }

    useEffect(() => {
        if (ticker) {
            fetch(`https://api.aletheiaapi.com/StockData?symbol=${ticker}&field=LongName,Price,Currency,MarketCap,PriceEarningsRatio,ForwardDividendYield,TrailingAnnualDividendYield,FiveYearAverageDividendYield,DividendPayoutRatio,Volume,Sector,Industry,Open,Exchange,EarningsDate,ProfitMargin,OperatingMargin,ReturnOnAssets,ReturnOnEquity,CashPerShare,DebtToEquityRatio,BookValuePerShare,ShortRatio`, {
                headers: {key: '2CD5D7C1C59B4966AB79930611BC7365'}
            }).then(res => res.json()).then(data => {
                console.log(data);
            })
        }
    }, [ticker])

    return <MDBTable maxHeight='25rem' className="heldTable" rules='none' scrollY border='1'>
        <MDBTableHead>
            <tr className='heldToprow'>
                <td className="heldTd">Company</td>
                <td className="heldTd">Ticker</td>
                <td className="heldTd">Price</td>
                <td className="heldTd">Weight</td>
            </tr>
        </MDBTableHead>
        <MDBTableBody>
            {spy.map((stock, i) => {
                return <tr key={i} className="heldTr"
                    id={stock.Symbol}
                    onClick={() => spyTableClick(stock.Symbol)}
                    onMouseOver={() => changeColor(stock.Symbol, false)}
					onMouseOut={() => changeColor(stock.Symbol, true)}
                >
                    <td className="heldTd">{stock.Company}</td>
                    <td className="heldTd">{stock.Symbol}</td>
                    <td className="heldTd">{stock.Price}</td>
                    <td className="heldTd">{stock.Weight.toFixed(2)}%</td>
                </tr>
            })
            
            }
        </MDBTableBody>
    </MDBTable>
}

export default SpyTable;
